import React, { useState } from "react";

//components
import Song from "./components/Song";
import Player from "./components/Player";
import Mode from "./components/Mode";

//data
import data from "./utils";

//styling
import "./styles/main.scss";

const App = () => {
  const [songs, setSongs] = useState(data());
  const [currentSong, setCurrentSong] = useState(songs[7]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  return (
    <div className={`${isDarkMode ? "light" : "dark"}`}>
      <Mode isDarkMode={isDarkMode} setIsDarkMode={setIsDarkMode} />
      <Song currentSong={currentSong} isDarkMode={isDarkMode} />
      <Player currentSong={currentSong} isDarkMode={isDarkMode} />
    </div>
  );
};

export default App;
